func newDocSeriesStep() *sqlgraph.Step {
	return sqlgraph.NewStep(
		sqlgraph.From(Table, FieldID),
		sqlgraph.To(DocSeriesInverseTable, FieldID),
		sqlgraph.Edge(sqlgraph.M2O, true, DocSeriesTable, DocSeriesColumn),
	)
}
