// OnConflict allows configuring the `ON CONFLICT` / `ON DUPLICATE KEY` clause
// of the `INSERT` statement. For example:
//
//	client.FCirclePost.CreateBulk(builders...).
//		OnConflict(
//			// Update the row with the new values
//			// the was proposed for insertion.
//			sql.ResolveWithNewValues(),
//		).
//		// Override some of the fields with custom
//		// update values.
//		Update(func(u *ent.FCirclePostUpsert) {
//			SetTitle(v+v).
//		}).
//		Exec(ctx)
func (fpcb *FCirclePostCreateBulk) OnConflict(opts ...sql.ConflictOption) *FCirclePostUpsertBulk {
	fpcb.conflict = opts
	return &FCirclePostUpsertBulk{
		create: fpcb,
	}
}

// OnConflictColumns calls `OnConflict` and configures the columns
// as conflict target. Using this option is equivalent to using:
//
//	client.FCirclePost.Create().
//		OnConflict(sql.ConflictColumns(columns...)).
//		Exec(ctx)
func (fpcb *FCirclePostCreateBulk) OnConflictColumns(columns ...string) *FCirclePostUpsertBulk {
	fpcb.conflict = append(fpcb.conflict, sql.ConflictColumns(columns...))
	return &FCirclePostUpsertBulk{
		create: fpcb,
	}
}

// FCirclePostUpsertBulk is the builder for "upsert"-ing
// a bulk of FCirclePost nodes.
type FCirclePostUpsertBulk struct {
	create *FCirclePostCreateBulk
}

// UpdateNewValues updates the mutable fields using the new values that
// were set on create. Using this option is equivalent to using:
//
//	client.FCirclePost.Create().
//		OnConflict(
//			sql.ResolveWithNewValues(),
//		).
//		Exec(ctx)
func (u *FCirclePostUpsertBulk) UpdateNewValues() *FCirclePostUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithNewValues())
	return u
}

// Ignore sets each column to itself in case of conflict.
// Using this option is equivalent to using:
//
//	client.FCirclePost.Create().
//		OnConflict(sql.ResolveWithIgnore()).
//		Exec(ctx)
func (u *FCirclePostUpsertBulk) Ignore() *FCirclePostUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWithIgnore())
	return u
}

// DoNothing configures the conflict_action to `DO NOTHING`.
// Supported only by SQLite and PostgreSQL.
func (u *FCirclePostUpsertBulk) DoNothing() *FCirclePostUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.DoNothing())
	return u
}

// Update allows overriding fields `UPDATE` values. See the FCirclePostCreateBulk.OnConflict
// documentation for more info.
func (u *FCirclePostUpsertBulk) Update(set func(*FCirclePostUpsert)) *FCirclePostUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&FCirclePostUpsert{UpdateSet: update})
	}))
	return u
}

